import React, { Component } from 'react';
import './App.css';
import { Route, Link } from 'react-router-dom'
import Homepage from './Homepage'
import Search from './Search'
import PocketDictionary from './PocketDictionary'

const api_key = '114ac045-9a24-4196-9a03-0f2e81a4f2f0'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      keyword: "",
      wordInfo: [],
      word: ""
    }
    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  handleChange(event){
    console.log("handleChange is running ", event.target.value)
    this.setState({ keyword: event.target.value })
  }

  handleSubmit(event){
    event.preventDefault()
    console.log("handleSubmit is running")
    this.getAPI()
  }

  getAPI(){
    const endPoint = `https://www.dictionaryapi.com/api/v3/references/collegiate/json/${this.state.keyword}?key=${api_key}`
    fetch(endPoint,
      {
        headers: {
          'Accept': 'application/json'
        }
      })
    .then(response => 
      response.json()
    )
    .then(json => {
      console.log('data', json)
      this.setState({ 
        wordInfo: json[0],
        word: json[0].meta
      })
    })
    .catch(e => 
      console.log(e)
    )
  }

  render() {
    return (
      <div className="App">
        <nav>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/search">Search</Link></li>
            <li><Link to="/pocket">Pocket Dictionary</Link></li>
          </ul>
            <Route exact path="/" component={Homepage}/>
            <Route path="/search"
              render={(props) => 
                <Search
                  keyword= {this.state.keyword}
                  handleChange={this.handleChange}
                  handleSubmit={this.handleSubmit}
                  {...props}/>}
            />
            <Route path="/pocket" component={PocketDictionary}/>
        </nav>
        <div>{this.state.word}</div>
      </div>
    );
  }
}

export default App;
